class NewPost extends Component {

     constructor(props) {
        super(props);
        this.state = {};
    }

    submitPost() {
        const url = `${API_URL}`
        const info = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(post),
        };
        fetch(url, info
             )
            .then(response => response.json())
    }


    render() {
        const {latest_posts,
              }
              = this.state;
        return JSON.stringify(this.state);
        if (!latest_posts) {
            return JSON.stringify(this.state);
        }
        return "hmm";
        return (
            <div className="page">
              <Helmet>
                <meta charSet="utf-8" />
                <title>MMW - Latest Posts</title>
              </Helmet>
              <hr/>
              <div className="cf fl">
                <div className="fl w-90 pr2 justify-center">
                  <div className="latest_posts">
                    <h2>latest posts</h2>
                    <ol className="list pl0 mt2">
                      {latest_posts.map(
                          (post) =>
                              <div className="post_mini">
                                <li>
                                  <h3>
                                    {post.title}
                                  </h3>
                                  <p>
                                    {post.content}
                                  </p>
                                </li>
                              </div>
                      )}
                    </ol>
                  </div>;

                </div>
              </div>
            </div>
        );
    }
}



export default NewPost;
