import {Helmet} from 'react-helmet';

// import striptags from 'striptags';

import Fetchers from '../utilities/fetchers.js';

// import {ExtendingList, ProfileMini} from '../../components/extending-list.js';
// import {RecommendationsSidebar, RecommendationsSidebarPanel, CommunityMini, TopicMini} from '../../components/recommendations-sidebar.js';

// import {TruncateHtml} from '../../utilities/formatting.js';



class LatestPosts extends Component {

    constructor(props) {
        super(props);

        this.state = {};

        this.fetchData = Fetchers.fetchData.bind(this);        

    }

    componentDidMount() {
        this.fetchData();
    }

    render() {
        const {latest_posts,
               more,
              }
              = this.state;
        if (!latest_posts) {
            return "loading...";
        }
        return (
            <div className="page">
              <Helmet>
                <meta charSet="utf-8" />
                <title>MMW - All Posts</title>
              </Helmet>
              <hr/>
              <div className="cf fl">
                <div className="fl w-90 pr2 justify-center">

                  {/* list of posts here */}

                  <div>
                    <h2>{headingStr}</h2>
                    {JSON.stringify(itemRenderer)}
                    <ol className="list pl0 mt2">
                      {contentsList.map(itemRenderer)}
                    </ol>
                    {more ?
                     <ListExtendButton
                       onClick={extender}
                       contentsList={contentsList}/>
                     : ""
                    }
                  </div>;


                </div>
              </div>
            </div>
        );
    }
}



export default LatestPosts
