import React, { Component } from 'react';
import {Helmet} from 'react-helmet';

// import striptags from 'striptags';

import Fetchers from '../utilities/fetchers.js';

// import {ExtendingList, ProfileMini} from '../../components/extending-list.js';
// import {RecommendationsSidebar, RecommendationsSidebarPanel, CommunityMini, TopicMini} from '../../components/recommendations-sidebar.js';

import {TruncateHtml} from '../../utilities/formatting.js';



class LatestPostsView extends Component {

    constructor(props) {
        super(props);

        this.state = {};
        this.title = "All Posts";

        this.fetchData = Fetchers.fetchData.bind(this);        
        this.fetchMoreData = Fetchers.fetchMoreData.bind(this);

    }

    componentDidMount() {
        this.fetchData();
    }

    render() {
        const {latest_posts,
               more,
              }
              = this.state;
        if (!latest_posts) {
            return "loading...";
        }
        return (
            <div className="page">
              <Helmet>
                <meta charSet="utf-8" />
                <title>MMW - {this.title}</title>
              </Helmet>
              <hr/>
              <div className="cf fl">
                <div className="fl w-90 pr2 justify-center">
                  <ExtendingList
                    headingStr="Latest posts"
                    contentsList={latest_posts}
                    itemRenderer={PostPreview}
                    extender={() =>
                              this.fetchMoreData('latest_posts', 10)
                             }
                    more={more}
                  />
                </div>
              </div>
            </div>
        );
    }
}
